'use strict';

function remove_accents(str) {
  return str.replace(/à/g, "a")
    .replace(/ï/g, "i")
    .replace(/[éèê]/g, "e")
    .replace(/ç/g, "c")
    .replace(/ù/g, "u");  
}

var prettyUrl = {};

prettyUrl.slug = function(str) {
  return remove_accents(str.toLowerCase()
    .replace(/ /g, "-")
    .replace(/[?!]/g, "")
    .replace(/_/g, "-")
    .replace(/['"]/g, "")
    .replace(/[(){}]/g, "")
    .replace(/\[/g, "")
    .replace(/\]/g, "")
  );
}

module.exports = prettyUrl;
