var prettyUrl = require('../pretty-url');
var expect = require('expect.js');

describe('Slug function', function() {
  it('returns the string in lowercase', function() {
    expect(prettyUrl.slug("ABC")).to.eql("abc");
  });

  it('converts spaces into hyphens', function() {
    expect(prettyUrl.slug("hello world")).to.eql("hello-world");
  });

  it('removes accent', function() {
    expect(prettyUrl.slug("Saïd")).to.eql("said");
    expect(prettyUrl.slug("Pokémon")).to.eql("pokemon");
    expect(prettyUrl.slug("Hernandès")).to.eql("hernandes");
    expect(prettyUrl.slug("garçon")).to.eql("garcon");
    expect(prettyUrl.slug("où")).to.eql("ou");
  });

  it('removes interrogation points', function() {
    expect(prettyUrl.slug("How are you?")).to.eql("how-are-you");
  });
  
  it('removes exclamation points', function() {
    expect(prettyUrl.slug("Here they are!")).to.eql("here-they-are");
  });

  it('converts underscore into hyphens', function() {
    expect(prettyUrl.slug("Ruby_on_rails")).to.eql("ruby-on-rails");
  });

  it('removes apostrophes', function() {
    expect(prettyUrl.slug("Lil' Wayne")).to.eql("lil-wayne");
  });

  it('removes all brackets', function() {
    expect(prettyUrl.slug("Hello (ok)")).to.eql("hello-ok");
    expect(prettyUrl.slug("Hello {ok}")).to.eql("hello-ok");
    expect(prettyUrl.slug("Hello [ok]")).to.eql("hello-ok");
  });
});
